function createJwt(user) {
  return jwt.sign(
    {
      id: user.id,                 // MUST be UUID string
      email: user.email,
      role: user.role || "user",
      subscribed: !!user.subscribed,
    },
    JWT_SECRET,
    { expiresIn: "7d" }
  );
}

// Back-compat alias (older code may call issueJwt)
