function requireExportAccess(req, res, next, toolName) {
  if (PAYWALL_DISABLED) return next();

  const user = getUserFromRequest(req);
  const ppeSession = req.headers["x-ppe-session"];

  if (user && user.subscribed) return next();
  if (ppeSession) return next();

  return res.status(402).json({
    error: "Export requires payment or subscription.",
    tool: toolName,
  });
}

async function dbHasColumn(table, column) {
  const q = `
    SELECT 1
    FROM information_schema.columns
    WHERE table_schema='public' AND table_name=$1 AND column_name=$2
    LIMIT 1
  `;
  const r = await pool.query(q, [table, column]);
  return r.rowCount > 0;
}

async function dbHasTable(table) {
  const q = `
    SELECT 1
    FROM information_schema.tables
    WHERE table_schema='public' AND table_name=$1
    LIMIT 1
  `;
  const r = await pool.query(q, [table]);
  return r.rowCount > 0;
}

async function safeQuery(sql, params) {
  try {
    return await pool.query(sql, params);
  } catch (e) {
    // bubble up with context
    e._sql = sql;
    throw e;
  }
}

/**
 * getSubscriptionStatus(userId) -> boolean
 * Best-effort subscription lookup. Never throws.
 * Dev default: true (so you can use the app without Stripe set up).
 * Prod default: false unless we can verify an active subscription row.
 */
async function getSubscriptionStatus(userId) {
  const devDefault = (process.env.NODE_ENV || "development") !== "production";
  try {
    if (!(await dbHasTable("subscriptions"))) return devDefault;

    const hasStatus = await dbHasColumn("subscriptions", "status").catch(() => false);
    const hasIsActive = await dbHasColumn("subscriptions", "is_active").catch(() => false);
    const hasActive = await dbHasColumn("subscriptions", "active").catch(() => false);
    const hasCurrentPeriodEnd = await dbHasColumn("subscriptions", "current_period_end").catch(() => false);
    const hasEndsAt = await dbHasColumn("subscriptions", "ends_at").catch(() => false);
    const hasCancelAtPeriodEnd = await dbHasColumn("subscriptions", "cancel_at_period_end").catch(() => false);

    const where = "user_id = $1";
    const args = [userId];

    let q = `SELECT * FROM subscriptions WHERE ${where} ORDER BY created_at DESC NULLS LAST LIMIT 1`;
    if (!(await dbHasColumn("subscriptions", "created_at").catch(() => false))) {
      q = `SELECT * FROM subscriptions WHERE ${where} ORDER BY id DESC NULLS LAST LIMIT 1`;
    }

    const r = await safeQuery(q, args).catch(() => null);
    const row = r && r.rows && r.rows[0] ? r.rows[0] : null;
    if (!row) return false;

    if (hasIsActive && typeof row.is_active === "boolean") return row.is_active;
    if (hasActive && typeof row.active === "boolean") return row.active;

    if (hasStatus && row.status) {
      const s = String(row.status).toLowerCase();
      if (["active", "trialing", "past_due"].includes(s)) return true;
      if (["canceled", "cancelled", "incomplete", "incomplete_expired", "unpaid", "paused"].includes(s)) return false;
    }

    const now = Date.now();
    if (hasCurrentPeriodEnd && row.current_period_end) {
      const t = new Date(row.current_period_end).getTime();
      if (!Number.isNaN(t) && t > now) return true;
    }
    if (hasEndsAt && row.ends_at) {
      const t = new Date(row.ends_at).getTime();
      if (!Number.isNaN(t)) return t > now;
    }

    if (hasCancelAtPeriodEnd && row.cancel_at_period_end === true) return true;

    // Row exists but schema unclear: assume subscribed to avoid locking out legit users.
    return true;
  } catch (e) {
    return devDefault;
  }
}

