// /api/login  (Postgres-backed)
app.post("/api/login", async (req, res) => {
  try {
    const { email, password } = req.body || {};
    const e = String(email || "").trim().toLowerCase();
    const p = String(password || "").trim();
    if (!e || !p) return res.status(400).json({ error: "Email and password required." });

    // Load user
    const r = await safeQuery(
      `SELECT id, email, password_hash, role, is_active
       FROM users
       WHERE email = $1
       LIMIT 1`,
      [e]
    );

    let u = r.rows[0];

    // DEV behavior: create user if not exists
    if (!u) {
      const hash = await bcrypt.hash(p, 12);
      const created = await safeQuery(
        `INSERT INTO users (email, password_hash, role, is_active)
         VALUES ($1, $2, 'user', true)
         RETURNING id, email, password_hash, role, is_active`,
        [e, hash]
      );
      u = created.rows[0];
    } else {
      if (u.is_active === false) return res.status(403).json({ error: "Account disabled." });

      if (!u.password_hash) {
        const hash = await bcrypt.hash(p, 12);
        const updated = await safeQuery(
          `UPDATE users SET password_hash=$2 WHERE id=$1
           RETURNING id, email, password_hash, role, is_active`,
          [u.id, hash]
        );
        u = updated.rows[0];
      } else {
        const ok = await bcrypt.compare(p, u.password_hash);
        if (!ok) return res.status(401).json({ error: "Invalid credentials." });
      }
    }

    // Subscription status (best-effort)
    const subscribed = await getSubscriptionStatus(u.id);

    const token = createJwt({ id: u.id, email: u.email, role: u.role, subscribed });

    // Persist session if table exists
    if (await dbHasTable("sessions")) {
      // try common columns
      const hasToken = await dbHasColumn("sessions", "token");
      const hasUserId = await dbHasColumn("sessions", "user_id");
      const hasCreatedAt = await dbHasColumn("sessions", "created_at");
      const hasExpiresAt = await dbHasColumn("sessions", "expires_at");

      if (hasUserId) {
        const cols = [];
        const vals = [];
        const args = [];

        cols.push("user_id"); vals.push(u.id); args.push(`$${args.length + 1}`);
        if (hasToken) { cols.push("token"); vals.push(token); args.push(`$${args.length + 1}`); }
        if (hasCreatedAt) { cols.push("created_at"); vals.push(new Date()); args.push(`$${args.length + 1}`); }
        if (hasExpiresAt) {
          const exp = new Date(Date.now() + 7 * 24 * 3600 * 1000);
          cols.push("expires_at"); vals.push(exp); args.push(`$${args.length + 1}`);
        }

        await safeQuery(
          `INSERT INTO sessions (${cols.join(",")}) VALUES (${args.join(",")})`,
          vals
        ).catch(() => {});
      }
    }

    res.json({ token });
  } catch (e) {
    console.error("login error:", e);
    res.status(500).json({
      error: "Login failed.",
      detail: String(e?.message || e),
      hint: e?._sql ? `Failed SQL: ${e._sql}` : undefined,
    });
  }
});

