app.post("/api/broker/settings", requireAuth, async (req, res) => {
  try {
    if (!(await dbHasTable("broker_settings"))) {
      return res.status(501).json({ error: "broker_settings table not found in DB." });
    }

    const incoming = req.body || {};
    const existing = await getBrokerSettings(req.user.id);

    // Upsert strategy using ON CONFLICT if constraint exists on (user_id).
    // If your table doesn't have unique(user_id), this will error and we’ll adjust.
    const q = `
      INSERT INTO broker_settings (user_id, data, updated_at)
      VALUES ($1, $2::jsonb, now())
      ON CONFLICT (user_id)
      DO UPDATE SET data = EXCLUDED.data, updated_at = now()
      RETURNING *
    `;

    // If your schema doesn't have "data jsonb", fall back to “wide columns” update.
    const hasData = await dbHasColumn("broker_settings", "data");

    if (hasData) {
      const data = { ...(existing?.data || {}), ...incoming };
      const r = await safeQuery(q, [req.user.id, JSON.stringify(data)]);
      return res.json({ ok: true, settings: r.rows[0] });
    }

    // Wide-column fallback (best-effort)
    // Only sets columns that exist.
    const allowed = [
      ["company_name", "companyName"],
      ["mc_number", "mcNumber"],
      ["dot_number", "dotNumber"],
      ["phone", "phone"],
      ["email", "email"],
      ["address", "address"],
      ["timezone", "timezone"],
      ["currency", "currency"],
      ["distance_units", "distanceUnits"],
    ];

    const cols = [];
    const vals = [req.user.id];
    const sets = [];
    let idx = 2;

    for (const [col, key] of allowed) {
      if (!(await dbHasColumn("broker_settings", col))) continue;
      if (incoming[key] === undefined && incoming[col] === undefined) continue;
      const v = incoming[key] !== undefined ? incoming[key] : incoming[col];
      cols.push(col);
      vals.push(v);
      sets.push(`${col}=$${idx++}`);
    }

    if (!existing) {
      // INSERT minimal
      const insertCols = ["user_id", ...cols];
      const insertArgs = insertCols.map((_, i) => `$${i + 1}`);
      const r = await safeQuery(
        `INSERT INTO broker_settings (${insertCols.join(",")}) VALUES (${insertArgs.join(",")}) RETURNING *`,
        [req.user.id, ...vals.slice(1)]
      );
      return res.json({ ok: true, settings: r.rows[0] });
    } else {
      if (!sets.length) return res.json({ ok: true, settings: existing });
      const r = await safeQuery(
        `UPDATE broker_settings SET ${sets.join(",")}, updated_at=now() WHERE user_id=$1 RETURNING *`,
        vals
      );
      return res.json({ ok: true, settings: r.rows[0] });
    }
  } catch (e) {
    console.error("broker settings error:", e);
    res.status(500).json({ error: String(e?.message || e) });
  }
});

