app.get("/api/broker/loads", requireAuth, async (req, res) => {
  try {
    if (!(await dbHasTable("broker_loads"))) return res.json({ ok: true, loads: [] });

    const q = String(req.query.q || "").trim().toLowerCase();

    // common fields: id, user_id, reference, shipper, consignee, pickup, delivery, equipment, rate, currency, status, notes, last_location, at_receiver, geofence, created_at, updated_at
    // If your schema uses jsonb for load data, adjust later.
    let sql = `SELECT * FROM broker_loads WHERE user_id=$1`;
    const params = [req.user.id];

    if (q) {
      // best-effort search on text columns if present
      const searchCols = [];
      for (const c of ["reference","shipper","consignee","status","equipment"]) {
        if (await dbHasColumn("broker_loads", c)) searchCols.push(`${c}::text ILIKE $2`);
      }
      if (searchCols.length) {
        sql += ` AND (${searchCols.join(" OR ")})`;
        params.push(`%${q}%`);
      }
    }

    // order
    if (await dbHasColumn("broker_loads", "created_at")) sql += ` ORDER BY created_at DESC`;
    const r = await safeQuery(sql, params);
    res.json({ ok: true, loads: r.rows });
  } catch (e) {
    res.status(500).json({ error: String(e?.message || e) });
  }
});

app.post("/api/broker/loads", requireAuth, async (req, res) => {
  try {
    if (!(await dbHasTable("broker_loads"))) return res.status(501).json({ ok: false, error: "broker_loads table not found" });

    const userId = req.user.id;
    const body = req.body || {};

    const allowed = [
      "load_number",
      "reference",
      "status",
      "shipper_id",
      "carrier_id",
      "shipper_name",
      "carrier_name",
      "origin",
      "destination",
      "pickup_date",
      "delivery_date",
      "equipment",
      "weight",
      "rate_cents",
      "revenue_cents",
      "cost_cents",
      "margin_cents",
      "notes",
      "data"
    ];

    const cols = [];
    const vals = [];
    const args = [];

    // Mandatory owner column if present
    if (await dbHasColumn("broker_loads", "user_id")) {
      cols.push("user_id");
      args.push(userId);
      vals.push(`$${args.length}`);
    }

    // Wide columns
    for (const k of allowed) {
      if (body[k] === undefined || body[k] === null) continue;
      if (!(await dbHasColumn("broker_loads", k))) continue;
      cols.push(k);
      args.push(body[k]);
      vals.push(`$${args.length}`);
    }

    // JSONB payload column support
    const hasData = await dbHasColumn("broker_loads", "data");
    if (hasData) {
      const extra = { ...(typeof body.data === "object" && body.data ? body.data : {}) };
      for (const [k, v] of Object.entries(body)) {
        if (k === "data" || k === "user_id" || k === "id") continue;
        if (allowed.includes(k) && (await dbHasColumn("broker_loads", k))) continue;
        extra[k] = v;
      }
      cols.push("data");
      args.push(extra);
      vals.push(`$${args.length}`);
    }

    // created_at default if present
    if (await dbHasColumn("broker_loads", "created_at")) {
      cols.push("created_at");
      vals.push("NOW()");
    }

    // Minimal insert if nothing else
    if (!cols.length) {
      const q = `INSERT INTO broker_loads DEFAULT VALUES RETURNING *`;
      const r = await safeQuery(q, []);
      return res.json({ ok: true, load: r.rows[0] });
    }

    const q = `INSERT INTO broker_loads (${cols.join(",")}) VALUES (${vals.join(",")}) RETURNING *`;
    const r = await safeQuery(q, args);
    res.json({ ok: true, load: r.rows[0] });
  } catch (e) {
    res.status(500).json({ ok: false, error: String(e.message || e) });
  }
});

