app.put("/api/broker/loads/:id", requireAuth, async (req, res) => {
  try {
    if (!(await dbHasTable("broker_loads"))) return res.status(501).json({ ok: false, error: "broker_loads table not found" });

    const userId = req.user.id;
    const id = req.params.id;
    const body = req.body || {};

    // Load existing row for JSON merge and ownership checks
    const ownerCol = (await dbHasColumn("broker_loads", "user_id")) ? "user_id" : null;
    const where = ownerCol ? `id=$1 AND ${ownerCol}=$2` : `id=$1`;
    const argsSel = ownerCol ? [id, userId] : [id];
    const existing = await safeQuery(`SELECT * FROM broker_loads WHERE ${where}`, argsSel);
    if (!existing.rows.length) return res.status(404).json({ ok: false, error: "Load not found" });
    const current = existing.rows[0];

    const allowed = [
      "load_number",
      "reference",
      "status",
      "shipper_id",
      "carrier_id",
      "shipper_name",
      "carrier_name",
      "origin",
      "destination",
      "pickup_date",
      "delivery_date",
      "equipment",
      "weight",
      "rate_cents",
      "revenue_cents",
      "cost_cents",
      "margin_cents",
      "notes",
      "data"
    ];

    const sets = [];
    const args = [];

    // Wide columns
    for (const k of allowed) {
      if (body[k] === undefined) continue;
      if (!(await dbHasColumn("broker_loads", k))) continue;
      sets.push(`${k}=$${args.length + 1}`);
      args.push(body[k]);
    }

    // JSON merge if supported
    const hasData = await dbHasColumn("broker_loads", "data");
    if (hasData) {
      const merged = { ...(current.data && typeof current.data === "object" ? current.data : {}) };
      const incoming = { ...(typeof body.data === "object" && body.data ? body.data : {}) };
      for (const [k, v] of Object.entries(body)) {
        if (k === "data" || k === "user_id" || k === "id") continue;
        if (allowed.includes(k) && (await dbHasColumn("broker_loads", k))) continue;
        incoming[k] = v;
      }
      Object.assign(merged, incoming);
      sets.push(`data=$${args.length + 1}`);
      args.push(merged);
    }

    if (await dbHasColumn("broker_loads", "updated_at")) {
      sets.push("updated_at=NOW()");
    }

    if (!sets.length) return res.json({ ok: true, load: current });

    const whereUpd = ownerCol ? `id=$${args.length + 1} AND ${ownerCol}=$${args.length + 2}` : `id=$${args.length + 1}`;
    args.push(id);
    if (ownerCol) args.push(userId);

    const q = `UPDATE broker_loads SET ${sets.join(", ")} WHERE ${whereUpd} RETURNING *`;
    const r = await safeQuery(q, args);
    res.json({ ok: true, load: r.rows[0] });
  } catch (e) {
    res.status(500).json({ ok: false, error: String(e.message || e) });
  }
});

