app.post("/api/broker/loads/:id/location", requireAuth, async (req, res) => {
  try {
    if (!(await dbHasTable("broker_loads"))) return res.status(404).json({ error: "Not found." });

    const lat = Number(req.body.lat);
    const lng = Number(req.body.lng);
    if (!Number.isFinite(lat) || !Number.isFinite(lng)) {
      return res.status(400).json({ error: "lat and lng are required numbers." });
    }

    const payload = { lat, lng, ts: req.body.ts ? String(req.body.ts) : nowIso() };

    const sets = [];
    const vals = [req.user.id, req.params.id];
    let i = 3;

    if (await dbHasColumn("broker_loads", "last_location")) {
      sets.push(`last_location=$${i++}::jsonb`);
      vals.push(JSON.stringify(payload));
    } else if (await dbHasColumn("broker_loads", "lastLocation")) {
      sets.push(`"lastLocation"=$${i++}::jsonb`);
      vals.push(JSON.stringify(payload));
    }

    if (req.body.atReceiver !== undefined) {
      if (await dbHasColumn("broker_loads","at_receiver")) { sets.push(`at_receiver=$${i++}`); vals.push(!!req.body.atReceiver); }
      else if (await dbHasColumn("broker_loads","atReceiver")) { sets.push(`"atReceiver"=$${i++}`); vals.push(!!req.body.atReceiver); }
    }

    if (req.body.geofence !== undefined) {
      if (await dbHasColumn("broker_loads","geofence")) { sets.push(`geofence=$${i++}::jsonb`); vals.push(JSON.stringify(req.body.geofence)); }
    }

    if (await dbHasColumn("broker_loads","updated_at")) sets.push(`updated_at=now()`);

    if (!sets.length) return res.status(501).json({ error: "broker_loads tracking columns not found in schema." });

    const r = await safeQuery(
      `UPDATE broker_loads SET ${sets.join(", ")} WHERE user_id=$1 AND id=$2 RETURNING *`,
      vals
    );
    if (!r.rows[0]) return res.status(404).json({ error: "Load not found." });
    res.json({ ok: true, load: r.rows[0] });
  } catch (e) {
    res.status(500).json({ error: String(e?.message || e) });
  }
});

// -------------------- PDF helpers --------------------
async function loadPdfFromBuffer(buf) { return await PDFDocument.load(buf); }
async function savePdf(doc) { return Buffer.from(await doc.save()); }

function makeSimpleDocPdf(kind, body) {
  const doc = new PDFKitDocument({ margin: 50 });
  const chunks = [];
  doc.on("data", (d) => chunks.push(d));

  const title = kind === "invoice" ? "INVOICE" : kind === "receipt" ? "RECEIPT" : "PAYSTUB";
  doc.fontSize(20).text(title, { align: "right" });
  doc.moveDown();
  doc.fontSize(10).fillColor("#555");
  doc.text(new Date().toLocaleString(), { align: "right" });
  doc.moveDown(2);

  doc.fillColor("#000");
  Object.entries(body || {}).forEach(([k, v]) => {
    doc.font("Helvetica-Bold").text(k + ":", { continued: true });
    doc.font("Helvetica").text(" " + String(v || ""));
  });

  doc.end();
  return new Promise((resolve) => doc.on("end", () => resolve(Buffer.concat(chunks))));
}

async function handleSimpleDoc(kind, req, res) {
  const buf = await makeSimpleDocPdf(kind, req.body || {});
  res.setHeader("Content-Type", "application/pdf");
  res.send(buf);
}

