const inMemBrokerData = {}; // fallback if broker_settings does not exist

async function getBrokerData(userId) {
  if (!(await dbHasTable("broker_settings"))) {
    if (!inMemBrokerData[userId]) inMemBrokerData[userId] = {};
    return inMemBrokerData[userId];
  }

  const row = await safeQuery("SELECT * FROM broker_settings WHERE user_id=$1 LIMIT 1", [userId]).then(r => r.rows?.[0] || null);
  const obj = (row && row.data && typeof row.data === "object") ? row.data : {};
  return {
    carriers: Array.isArray(obj.carriers) ? obj.carriers : [],
    shippers: Array.isArray(obj.shippers) ? obj.shippers : [],
    workflow: obj.workflow && typeof obj.workflow === "object" ? obj.workflow : { rules: [] },
    edi: obj.edi && typeof obj.edi === "object" ? obj.edi : { messages: [] }
  };
}

async function saveBrokerData(userId, nextData) {
  if (!(await dbHasTable("broker_settings"))) {
    inMemBrokerData[userId] = nextData;
    return nextData;
  }

  const dataCol = (await dbHasColumn("broker_settings", "data")) ? "data" : null;
  if (!dataCol) throw new Error("broker_settings.data column missing");

  const exists = await safeQuery("SELECT 1 FROM broker_settings WHERE user_id=$1", [userId]).then(r => r.rows.length > 0);

  if (exists) {
    const sets = [`${dataCol}=$1`];
    if (await dbHasColumn("broker_settings", "updated_at")) sets.push("updated_at=NOW()");
    const q = `UPDATE broker_settings SET ${sets.join(", ")} WHERE user_id=$2 RETURNING *`;
    const r = await safeQuery(q, [nextData, userId]);
    return r.rows[0];
  } else {
    const cols = ["user_id", dataCol];
    const vals = ["$1", "$2"];
    const args = [userId, nextData];
    if (await dbHasColumn("broker_settings", "created_at")) { cols.push("created_at"); vals.push("NOW()"); }
    if (await dbHasColumn("broker_settings", "updated_at")) { cols.push("updated_at"); vals.push("NOW()"); }
    const q = `INSERT INTO broker_settings (${cols.join(",")}) VALUES (${vals.join(",")}) RETURNING *`;
    const r = await safeQuery(q, args);
    return r.rows[0];
  }
}

function withId(obj) {
  const out = { ...obj };
  if (!out.id) out.id = crypto.randomUUID();
  return out;
}

